import React, { useState } from 'react';
import { View, Text, StyleSheet, TextInput, Pressable } from 'react-native';

export default function EnterGoodsScreen() {
  const [category, setCategory] = useState('');
  const [place, setPlace] = useState('');
  const [description, setDescription] = useState('');
  const [productName, setProductName] = useState('');
  const [quantity, setQuantity] = useState('');

  const handleSave = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/enter-goods/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          store: 1,
          product: {
            name: productName,
            location_in_store: place,
            description,
            price: 0.0,
            category: {
              name: category,
            },
          },
          available_quantity: parseInt(quantity),
          date_received: new Date().toISOString().split('T')[0],
          expiration_date: null,
          date_retrieved: null,
        }),
      });

      if (response.ok) {
        // Goods successfully entered
        setCategory('');
        setPlace('');
        setDescription('');
        setProductName('');
        setQuantity('');
      } else {
        // Handle error response from the server
        console.log('Error:', response.status);
      }
    } catch (error) {
      // Handle network errors
      console.log('Error:', error.message);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.headerText}>Enter Goods</Text>
      <TextInput
        style={styles.inputBox}
        value={category}
        onChangeText={setCategory}
        placeholder="Category"
      />
      <TextInput
        style={styles.inputBox}
        value={place}
        onChangeText={setPlace}
        placeholder="Place"
      />
      <TextInput
        style={styles.inputBox}
        value={description}
        onChangeText={setDescription}
        placeholder="Description"
      />
      <TextInput
        style={styles.inputBox}
        value={productName}
        onChangeText={setProductName}
        placeholder="Product Name"
      />
      <TextInput
        style={styles.inputBox}
        value={quantity}
        onChangeText={setQuantity}
        placeholder="Quantity"
        keyboardType="numeric"
      />
      <Pressable style={styles.button} onPress={handleSave}>
        <Text style={styles.buttonText}>Save</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#F5F5F5',
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  inputBox: {
    width: '80%',
    height: 40,
    borderWidth: 1,
    borderColor: '#333333',
    borderRadius: 8,
    paddingHorizontal: 10,
    marginBottom: 20,
    backgroundColor: '#FFFFFF',
  },
  button: {
    backgroundColor: '#4287f5',
    borderRadius: 8,
    paddingVertical: 12,
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  buttonText: {
    color: '#FFFFFF',
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  },
});
