import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, FlatList, Pressable } from 'react-native';

export default function RetrieveGoodsScreen({ navigation }) {
  const [stores, setStores] = useState([]);
  const [selectedStore, setSelectedStore] = useState(null);
  const [goodsData, setGoodsData] = useState([]);
  const [selectedGoods, setSelectedGoods] = useState([]);

  useEffect(() => {
    fetchStoresData();
  }, []);

  const fetchStoresData = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/stores/');
      const data = await response.json();
      if (Array.isArray(data)) {
        const storesData = data.map(store => ({
          id: store.store_id,
          name: store.store_name,
        }));
        setStores(storesData);
      } else {
        throw new Error('Data is not an array.');
      }
    } catch (error) {
      console.error('Error fetching stores data:', error);
    }
  };

  const fetchGoodsData = async () => {
    if (selectedStore) {
      try {
        const response = await fetch(`http://127.0.0.1:8000/api/retrieve-goods/?store=${selectedStore.id}`);
        const data = await response.json();
        if (Array.isArray(data)) {
          setGoodsData(data);
        } else {
          throw new Error('Goods data is not an array.');
        }
      } catch (error) {
        console.error('Error fetching goods data:', error);
      }
    }
  };

  useEffect(() => {
    fetchGoodsData();
  }, [selectedStore]);

  const handleSelectStore = (store) => {
    setSelectedStore(store);
    setGoodsData([]);
    setSelectedGoods([]);
  };

  const handleSelectGoods = (item) => {
    const isSelected = selectedGoods.some((selectedItem) => selectedItem.id === item.id);

    if (isSelected) {
      const updatedGoods = selectedGoods.filter((selectedItem) => selectedItem.id !== item.id);
      setSelectedGoods(updatedGoods);
    } else {
      setSelectedGoods([...selectedGoods, item]);
    }
  };

  const handleShipping = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/shipping/', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(selectedGoods),
      });

      if (response.ok) {
        // Shipping successful
        setSelectedGoods([]);
        // Navigate to the desired screen after shipping (e.g., ConfirmationScreen)
        // navigation.navigate('ConfirmationScreen');
      } else {
        console.log('Error:', response.status);
      }
    } catch (error) {
      console.log('Error:', error.message);
    }
  };


  return (
    <View style={styles.container}>
      <Text style={styles.headerText}>Select a Store</Text>
      <FlatList
        data={stores}
        keyExtractor={(item) => (item.id ? item.id.toString() : Math.random().toString())}
        renderItem={({ item }) => (
          <Pressable
            style={[styles.storeContainer, selectedStore && selectedStore.id === item.id && styles.selectedStoreContainer]}
            onPress={() => handleSelectStore(item)}
          >
            <Text style={styles.storeName}>{item.name}</Text>
          </Pressable>
        )}
      />
      {selectedStore && (
        <>
          <Text style={styles.headerText}>Select Goods to Retrieve</Text>
          <FlatList
            data={goodsData}
            keyExtractor={(item) => item.id.toString()}
            renderItem={({ item }) => (
              <Pressable
                style={[styles.itemContainer, selectedGoods.includes(item) && styles.selectedItemContainer]}
                onPress={() => handleSelectGoods(item)}
              >
                <Text style={styles.itemName}>{item.name}</Text>
              </Pressable>
            )}
          />
          <Pressable
            style={styles.shippingButton}
            onPress={handleShipping}
            disabled={selectedGoods.length === 0}
          >
            <Text style={styles.shippingButtonText}>Ship</Text>
          </Pressable>
        </>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  headerText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  storeContainer: {
    backgroundColor: '#f2f2f2',
    padding: 10,
    borderRadius: 5,
    marginBottom: 10,
  },
  selectedStoreContainer: {
    backgroundColor: 'lightblue',
  },
  storeName: {
    fontSize: 16,
  },
  itemContainer: {
    backgroundColor: '#f2f2f2',
    padding: 10,
    borderRadius: 5,
    marginBottom: 10,
  },
  selectedItemContainer: {
    backgroundColor: 'lightblue',
  },
  itemName: {
    fontSize: 16,
  },
  shippingButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
    marginTop: 20,
  },
  shippingButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
});
