import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Picker, Pressable } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';

export default function RetrieveGoodsScreen() {
  const [stores, setStores] = useState([]);
  const [selectedStore, setSelectedStore] = useState(''); // Initialize selectedStore with an empty string
  const [goodsData, setGoodsData] = useState([]);
  const [selectedGoods, setSelectedGoods] = useState([]);
  const navigation = useNavigation();

  useEffect(() => {
    fetchStoresData();
  }, []);

  const fetchStoresData = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/stores/');
      const data = await response.json();
      if (Array.isArray(data)) {
        const storesData = data.map(store => ({
          id: store.store_id,
          name: store.store_name,
        }));
        setStores(storesData);
      } else {
        throw new Error('Data is not an array.');
      }
    } catch (error) {
      console.error('Error fetching stores data:', error);
    }
  };

  const fetchGoodsData = async () => {
    if (selectedStore) {
      try {
        const authToken = await AsyncStorage.getItem('authToken');
        if (!authToken) {
          navigation.navigate('Login');
          return;
        }

        const response = await fetch(`http://127.0.0.1:8000/api/retrieve-goods/?store=${selectedStore}`, {
          headers: {
            Authorization: `Token ${authToken}`,
          },
        });
        const data = await response.json();
        if (Array.isArray(data)) {
          setGoodsData(data);
        } else {
          throw new Error('Goods data is not an array.');
        }
      } catch (error) {
        console.error('Error fetching goods data:', error);
      }
    }
  };

  useEffect(() => {
    fetchGoodsData();
  }, [selectedStore]);

  const handleSelectStore = (store) => {
    setSelectedStore(store.id); // Update selectedStore with store.id
    setGoodsData([]);
    setSelectedGoods([]);
  };

  const handleSelectGoods = (item) => {
    const isSelected = selectedGoods.some((selectedItem) => selectedItem.id === item.id);

    if (isSelected) {
      const updatedGoods = selectedGoods.filter((selectedItem) => selectedItem.id !== item.id);
      setSelectedGoods(updatedGoods);
    } else {
      setSelectedGoods([...selectedGoods, item]);
    }
  };

  const handleShipping = async () => {
    try {
      const authToken = await AsyncStorage.getItem('authToken');
      if (!authToken) {
        navigation.navigate('Login');
        return;
      }

      const response = await fetch('http://127.0.0.1:8000/api/shipping/', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Token ${authToken}`,
        },
        body: JSON.stringify(selectedGoods),
      });

      if (response.ok) {
        // Shipping successful
        setSelectedGoods([]);
        fetchGoodsData(); // Refresh the goods data after shipping
      } else {
        throw new Error('Error: ' + response.status);
      }
    } catch (error) {
      console.error('Error shipping goods:', error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.headerText}>Select a Store</Text>
      <Picker
        style={styles.picker}
        selectedValue={selectedStore}
        onValueChange={(itemValue) => handleSelectStore(itemValue)}
      >
        <Picker.Item label="Select a store" value="" />
        {stores.map(store => (
          <Picker.Item key={store.id} label={store.name} value={store} />
        ))}
      </Picker>
      {selectedStore && (
        <>
          <Text style={styles.headerText}>Select Goods to Retrieve</Text>
          <FlatList
            data={goodsData}
            keyExtractor={(item) => item.id.toString()}
            renderItem={({ item }) => (
              <Pressable
                style={[styles.goodsItem, selectedGoods.some((selectedItem) => selectedItem.id === item.id) && styles.selectedGoodsItem]}
                onPress={() => handleSelectGoods(item)}
              >
                <Text style={styles.goodsName}>{item.name}</Text>
                <Text style={styles.goodsQuantity}>{item.quantity}</Text>
              </Pressable>
            )}
          />
          <Pressable
            style={styles.shipButton}
            onPress={handleShipping}
            disabled={selectedGoods.length === 0}
          >
            <Text style={styles.shipButtonText}>Ship</Text>
          </Pressable>
        </>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#F5F5F5',
    paddingHorizontal: 16,
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textShadowColor: 'rgba(0, 0, 0, 0.25)',
    textShadowOffset: { width: 0, height: 2 },
    textShadowRadius: 4,
  },
  picker: {
    width: '100%',
    marginBottom: 20,
    backgroundColor: '#FFFFFF',
    borderRadius: 8,
    elevation: 4,
    shadowColor: '#000',
    shadowOpacity: 0.25,
    shadowRadius: 4,
    shadowOffset: { width: 0, height: 2 },
  },
  goodsItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#FFFFFF',
    padding: 16,
    borderRadius: 8,
    marginBottom: 12,
    elevation: 4,
    shadowColor: '#000',
    shadowOpacity: 0.25,
    shadowRadius: 4,
    shadowOffset: { width: 0, height: 2 },
  },
  selectedGoodsItem: {
    backgroundColor: '#DDDDDD',
  },
  goodsName: {
    fontSize: 16,
  },
  goodsQuantity: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  shipButton: {
    backgroundColor: '#333333',
    borderRadius: 8,
    paddingVertical: 12,
    paddingHorizontal: 20,
    marginTop: 20,
    elevation: 4,
    shadowColor: '#000',
    shadowOpacity: 0.25,
    shadowRadius: 4,
    shadowOffset: { width: 0, height: 2 },
  },
  shipButtonText: {
    color: '#FFFFFF',
    fontSize: 18,
    fontWeight: 'bold',
    letterSpacing: 2,
    textTransform: 'uppercase',
  },
});
