import React, { useState, useEffect } from 'react';
import { ScrollView, Text, StyleSheet, TextInput, Pressable, View, Alert, Modal, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Picker } from '@react-native-picker/picker';
import axios from 'axios';
import { FileSystem } from 'react-native-fs'; // Import FileSystem from the 'react-native-fs' package



export default function SignUp() {
  const navigation = useNavigation();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('manager');
  const [rePassword, setRePassword] = useState('');
  const [selectedStore, setSelectedStore] = useState(null);
  const [stores, setStores] = useState([]);
  const [error, setError] = useState('');




  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/store/')
      .then(response => {
        const storeNames = response.data.map(store => store.name);
        setStores(storeNames);
        return FileSystem.writeFile(DATA_FILE_PATH, JSON.stringify(storeData), 'utf8');

      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  function handleSignUp() {
    setError('');

    let formData = new FormData();
    formData.append('username', email.toLowerCase());
    formData.append('password', password);
    formData.append('re_password', rePassword);
    formData.append('role', role);
    formData.append('store', selectedStore);

    fetch('http://127.0.0.1:8000/auth/users/', {
      method: 'POST',
      body: formData,
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error: ' + response.status);
        }
      })
      .then(data => {
        console.log(data);
        // Show success message to the user
        Alert.alert('Sign Up Successful', 'You have successfully signed up!');
        // Navigate to the desired screen
        navigation.navigate('kpiscreen'); // Replace 'kpiscreen' with the actual screen name for the KPI screen
      })
      .catch(error => {
        console.error(error);
        setError('An error occurred during sign up.');

        // Display the error message returned from the server
        if (error instanceof Error) {
          Alert.alert('Sign Up Error', error.message);
        } else {
          Alert.alert('Sign Up Error', 'An error occurred during sign up.');
        }
      });
  }

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.headerText}>Inventory Management</Text>
      <Text style={styles.regularText}>Sign Up to continue</Text>
      <TextInput
        style={styles.inputBox}
        value={email}
        onChangeText={setEmail}
        placeholder={'Email'}
        keyboardType={'email-address'}
      />
      <TextInput
        style={styles.inputBox}
        value={password}
        onChangeText={setPassword}
        placeholder={'Password'}
        secureTextEntry={true}
      />
      <TextInput
        style={styles.inputBox}
        value={rePassword}
        onChangeText={setRePassword}
        placeholder={'Retype Password'}
        secureTextEntry={true}
      />
      {error !== '' && <Text style={styles.errorText}>{error}</Text>}
      <View style={styles.pickerContainer}>
        <Text style={styles.pickerLabel}>Store:</Text>
        <Picker
          style={styles.picker}
          selectedValue={selectedStore}
          onValueChange={itemValue => setSelectedStore(itemValue)}
        >
          <Picker.Item label="Select a store" value="" />
          {stores.map(store => (
            <Picker.Item key={store} label={store} value={store} />
          ))}
        </Picker>
      </View>
      <View style={styles.pickerContainer}>
  <Text style={styles.pickerLabel}>Role:</Text>
  <Picker
    style={styles.picker}
    selectedValue={role}
    onValueChange={itemValue => setRole(itemValue)}
  >
    {['Manager', 'Receiver', 'Retriever'].map((label, index) => (
      <Picker.Item key={index} label={label} value={label.toLowerCase()} />
    ))}
  </Picker>
</View>
      <Pressable style={styles.button} onPress={handleSignUp}>
        <Text style={styles.buttonText}>Sign Up</Text>
      </Pressable>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
  },
  headerText: {
    padding: 40,
    fontSize: 30,
    color: '#333333',
    textAlign: 'center',
  },
  regularText: {
    fontSize: 24,
    padding: 20,
    marginVertical: 8,
    color: '#333333',
    textAlign: 'center',
  },
  inputBox: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    fontSize: 16,
    borderColor: '#333333',
    backgroundColor: '#FFFFFF',
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  pickerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginHorizontal: 12,
  },
  pickerLabel: {
    fontSize: 16,
    fontWeight: 'bold',
    marginRight: 8,
    marginBottom: 30,
  },
  picker: {
    flex: 1,
    borderWidth: 1,
    borderColor: '#333333',
    backgroundColor: '#FFFFFF',
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  button: {
    marginHorizontal: 20,
    marginBottom: 10,
    backgroundColor: '#645bff',
    paddingVertical: 12,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  buttonText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#FFFFFF',
    textAlign: 'center',
  },
  errorText: {
    fontSize: 16,
    color: 'red',
    textAlign: 'center',
    marginBottom: 10,
  },
});
